def main():
  n = ""
  adjacent_len = 13
  zero_count = n[0:adjacent_len].count('0')


  prev_prod = 1
  for digit in n[0:adjacent_len]:
    if digit != 0:
      prev_prod *= int(digit)
  biggest = 0 if zero_count > 0 else prev_prod
  
  for i in range(len(n)-adjacent_len):
    left = int(n[i])
    right = int(n[i+adjacent_len])

    zero_count = zero_count - (left == 0) + (right == 0)

    if left != 0:
      next_product = prev_prod / left
    if right != 0:
      next_product = next_product * right
    if zero_count == 0:
      biggest = max(biggest, next_product)

    prev_prod = next_product
  print(biggest)

main()